declare module "exif-parser" {
  export interface ExifTags {
    GPSLatitude?: number;
    GPSLongitude?: number;
    [key: string]: any;
  }

  export interface ExifResult {
    tags: ExifTags;
  }

  export class ExifParser {
    static create(buffer: Buffer): ExifParser;
    parse(): ExifResult;
  }

  export default ExifParser;
}
